package com.JavaDemo;

public class JavaCodeBreakAndcontinue {
// Here in this we have make use of break and continue 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=10; i++)
        {
       	 if(i == 1)
       	 {
       		 continue;   // break;
       	 }
       	 System.out.println(i);
        }

	}

}
