package com.JavaDemo;

public class JavaCodeNestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// here in this code we have make use of nested loop  which means loop inside a loop
		for(int i=1; i<=10; i++)
		{
			for(int j=1; j<=10; j++)
			{
	System.out.println("Value of i:"+i+" and value of j:"+j);
			}
			System.out.println("************************");
		}


	}

}
